import { useState } from 'react';

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      console.log('Enter key pressed. Search term:', searchTerm);
    }
  };

  return (
    <div className='flex items-center'>
      <input
        placeholder='Search'
        className='w-80 h-10 border border-gray-500 rounded-full bg-gray-200 px-4 mr-2'
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        onKeyDown={handleKeyPress} // Handle key press event
      />
      <div className='bg-gray-200 p-2 rounded-full'>
        {/* Icon component goes here */}
      </div>
    </div>
  );
}

export default SearchBar;
